import "../styles/description.css";

function Description() {
  return (
    <div className="container m-0 p">
      <div className="description-content">
        <h3>A propos de ce blog</h3>

        <p>
          Salut ! J'ai décidé de faire ce guide pour les nouveaux arrivant dans
          le 8ème arrondissement de Lyon. J'y habite depuis quelques années
          maintenant et j'avais à coeur de partager les endroits que j'apprécie
          à d'autres qui sauront j'espère les apprécier également. Il y a
          probablement d'autres lieux que je ne connais pas et qui sont sympa,
          mais ceux mentionnés, c'est du solide, de l'éprouvé ! La plupart des
          bars mentionnées dans ce blog servent à manger, j'ai décidé de les
          séparer des "restaux" à proprement parler lorsqu'il s'agit d'endroit à
          ambiance où il est cool d'aller boire un verre avec ses potes.
        </p>
      </div>
    </div>
  );
}

export default Description;
