import '../styles/barList.css'
import restaurant1 from '../assets/restaurant1.jpg'

function BarList() {

    const bars = [

        {name : "Kaffee Berlin", adress : "26 Cr Albert Thomas, 69008 Lyon", description : "Bar très sympa qui vends de la bière Allemande, l'équipe est au petit soin pour les clients. En plus si vous avez faim ils servent à manger, je recommande les burgers qui sont excellents", priceLevel : "€", photo : restaurant1}, 

        {name : "Ninkasi Sans Souci", adress : "26 Cr Albert Thomas, 69008 Lyon", description : "On ne présente plus la chaîne Lyonnaise qui est un incontournable d", priceLevel : "€", photo : restaurant1}, 

    ];

    return (
        <div>
            {bars.map((bar, index) => (

                <div key={index} className="bar-label">
                    <h3>{bar.name}</h3>
                    <p>Adresse : {bar.adress}</p>
                    <p>Niveau de prix : {bar.priceLevel}</p> 
                    <img className ="photo-bar" src ={bar.photo} alt={bar.name} />
                </div>         
            ))}
        </div>

    )
}

export default BarList
